sys_variable_value.value:
(function execute(inputs, outputs) {
	var responseObject = JSON.parse(inputs.response_body);
    var returned_temperature = responseObject.thermostatList[0].runtime.actualTemperature;
	outputs.parsed_temperature = returned_temperature / 10;
})(inputs, outputs);

